import React, { useState } from 'react';
import { useClerk } from '@clerk/clerk-react';
import './style1.css';

function Chat() {
  const { signOut } = useClerk();
  const [input, setInput] = useState(''); // Define input state
  const [messages, setMessages] = useState([]); // Define messages state

  const handleSend = async () => {
    if (!input) return; // Don't send if input is empty

    const newMessage = { role: 'user', content: input };
    setMessages((prevMessages) => [...prevMessages, newMessage]);
    setInput(''); // Clear input after sending

    // Call your API here to send the input to OpenAI
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input }),
      });
      const data = await response.json();

      // Add the AI's reply to messages
      const aiMessage = { role: 'assistant', content: data.reply };
      setMessages((prevMessages) => [...prevMessages, aiMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div>
      <nav className="navbar">
        <h1>Chat Support</h1>
      </nav>

      <div className="content">
        <h2>Chat with Us</h2>
        <div className="chat-box">
          {messages.map((msg, index) => (
            <div key={index} className={msg.role}>
              <strong>{msg.role === 'user' ? 'You' : 'AI'}:</strong> {msg.content}
            </div>
          ))}
        </div>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message here..."
        />
        <button onClick={handleSend}>Send</button>
      </div>

      <div className="button-container">
        <button onClick={() => signOut()}>Sign Out</button>
      </div>
    </div>
  );
}

export default Chat;
